sources.styles/components/nav/_ko-banner.scss:
.ko-wrapper {
  z-index: 95;
  background: white;
  position: relative;

}
.ko-banner-grid {
  display: none;
  justify-content: space-between;
  align-items: center;
  padding: 0.8em 0em;
  font-size: 0.9em;


  &__item {
    width: 25%;

    &:nth-child(2) {
      width: 50%;
      text-align: center;
    }

    &:nth-child(3) {
      text-align: right;
    }
  }

  @media(min-width: $bp-md) {
    display: flex;
  }
}

#banner-text {
  margin: 0;
  color: $primary;
  text-decoration: none;
  transition: color 400ms ease;
  display: inline-block;
  position: relative;

  &:after {
    content: '';
    position: absolute;
    bottom: 0; right: 0%;
    width: 0%;
    height: 1px; 
    background: $secondary;
    transition: width 300ms $ease-out-quart;
  }

  #promotional-text {
    display: inline-block;
    position: relative;
    transition: all 200ms ease;
  }

  &:hover {

    #promotional-text {
     transform: translateY(-4px);
    }

    &:after {
      right: auto; left: 0;
      width: 100%;
    }
  }
}


#ko-banner__social ul {
  margin: 0; padding: 0;
  list-style-type: none;
  display: flex;
  align-items: center;

  li {
    margin-right: 1.3em;
  }

  li:last-of-type {
    margin-right: 0em;
  }

  a {
    width: 22px;
    height: 22px;
    background: $gray;
    border-radius: 100%;
    position: relative;
    display: inline-block;
    transition: all 300ms ease;

    &:hover {
      background: $gray;

      svg {
        fill: $dark;
      }
    }
  }

  svg {
    width: 50%;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    position: absolute;
    fill: #999;
    transition: all 300ms ease;
  }

}


#user-nav ul{
  margin: 0; padding: 0;
  list-style-type: none;
  display: flex;
  justify-content: flex-end;
  align-items: center;

  li {
    margin-right: 1.8em;

    span {
      display: inline-block;
    }
  }

  li:last-of-type {
    margin-right: 0em;
  }

  a {
    color: $dark;
    text-decoration: none;
    display: flex;
    align-items: center;
    transition: all 600ms ease;

    &:hover {
      color: $primary;

      svg {
