sources.styles/pages/_product-single.scss:
.has-overlap {
  overflow: visible !important;
}

.product-single {
  display: flex;
  flex-direction: column;

  &__el {
    width: 100%;

    &:first-of-type {
      margin-bottom: 2em;
    }

    p, li {
      color: #444;
      font-size: 15px; 
    }
  }

  &__img {
    width: 100%;
    background: #f2f2f2;
  }

  .section-title {
    font-size: 1.8em;
    max-width: 500px;
    line-height: 1.7;
  }

  a {
    color: $primary;
    font-weight: 800;
    text-decoration: none;
  }
}

.selection {
  max-width: 350px;
}

.all-images {
  padding-top: 0.5em;
}


.all-images__preview {
  width: 25%;
}

#AddToCart {
  border: none;
  -webkit-appearance: none;
  cursor: pointer;
}

.product-hr {
  max-width: 80%;
  margin: 5em auto;
  height: 2px;
  background-color: #f2f2f2;
}

.qty-parent {
  display: flex;
  align-items: center;
  padding-bottom: 1em;

  label {
    margin-right: 8px;
  }
}

.product-section-content {
  padding: 2em 0em 2em 0em;

  .intro-content {
    padding-bottom: 2em;
    max-width: 600px;
    color: #999;
  }
}

.other-products {
  padding-bottom: 4em;
}

.show-case-image {
  width: 100%;
}

.show-case-image__inner {
  padding-bottom: 100%;
  position: relative;
}

.product-single__img {
 .flickity-prev-next-button {
   background-color: $primary;

   svg {
     fill: white;
   }
 }
}


.accordion-k {
  max-width: 900px;
  width: 90%;
  margin: auto;
  padding-top: 2em;
  padding-bottom: 5em;

  &__item {
    padding: 1.5em 0em;
    border-bottom: 1px solid #ddd;
    cursor: pointer;

    &:last-of-type {
      margin-bottom: 0;
    }
  }
}


.accordion-k__question {
  h3 {
    width: 90%;
    color: $primary;
  }
}


.accordion-k__answer {
  display: none;
  padding-bottom: 1em;

  p {
    margin: 0;
  }
}

@media(min-width: $bp-md) {
  .product-single {
    flex-direction: row;
    justify-content: space-between;

    &__el {
      width: 48%;

      &:first-of-type {
        margin-bottom: 0em;
      }
    }
  }
}

sources.styles/pages/_password-reset.scss:
.reset-panel {
  position: absolute;
  left: 50%;
  top: 50%;
  transform: translate(-50%, -50%);
  max-width: 600px;
  width: 100%;
  background: $gray;
  z-index: 999999;

  &__inner {
    padding: 2em 1.4em;
  }

  a {
    color: $dark;
    display: inline-block;
  }
}


.reset-form {
  padding: 1em 0em;

  label {
    display: inline-block;
    margin-bottom: 0.8em;
  }
  button {
    margin-top: 1em;
  }
}
.shopify-challenge__container {
  padding-top: 8em;
  padding-bottom: 4em;
}
button.go, .shopify-challenge__button, .go {
  @extend .cta;
  outline: none;
  border: none;
}

sources.styles/pages/_account.scss:
.orders__el {
  display: grid;
  grid-template-columns: 1fr;
  grid-row-gap: 40px;
  padding-top: 2em;

  @media(min-width: $bp-md) {
    grid-template-columns: 1fr 1fr 1fr;
    grid-gap: 40px;
  }

  @media(min-width: $bp-lg) {
    grid-template-columns: 1fr 1fr 1fr 1fr;
  }

}

.order-parent {
  padding: 1em 1em;
  border-bottom: 1px solid $gray;
  border-left: 2px solid $gray;

  &:nth-child(even) {
    border-left: 2px solid darken($gray, 15%);
  }
}

.orders__column {
  text-align: center;

  a {
    color: $dark;
  }
}

.account-shipping {
  display: flex;
}

.shipped-address {
  margin-left: 15px;
  p {
    margin: 0;
    display: inline;
  }

}

@media(min-width: $bp-md) {
  .check-status-total {
    text-align: right;

    a {
      color: $dark;
    }
  }
}


.user-settings {

  @media(min-width: $bp-md) {
    display: flex;
    justify-content: space-between;
  } 

  ul {
    display: flex;
    list-style-type: none;
    margin: 0; padding: 0;

    li {
      margin-left: 24px;
    }
  }

  a {
    color: $dark;
  }

}

sources.styles/pages/_login.scss:
.login-panel {
  padding-bottom: 2em;
  max-width: 800px;
  margin: auto;

  h3 {
    padding-bottom: 1em;
    text-align: center;
    font-size: 1.2em;

    a {
      color: $primary;
    }
  }

  label {
    display: inline-block;
    padding-bottom: 0.3em;
  }
}


.reset-password-form {
  display: none;
}

.form-btn-group {
  padding-top: 1em;
  display: flex;
  align-items: center;

  button {
    border: none;
    outline: none;
  }
}

.reset-password-form {
  padding-top: 2em;
}

.login-panel-parent button {
  border: none;
  outline: none;
}

#init-password-reset {
  display: inline-block;
  color: #aaa;
  margin-left: 1em;
  &:hover {
    transition: color 500ms ease;
    color: $dark;
  }
}

sources.styles/pages/_cart.scss:
.cart__column {
  margin-bottom: 1em;

  a {
    color: $dark;
  }

  h4 {
    padding-bottom: 0.8em;
  }

}

.checkout-more {
  padding-bottom: 4em;
}

.remove-item {
  font-size: 15px;
}

@media(min-width: $bp-md) {

  .cart__column {
    margin-bottom: 0em;
    text-align: center;

    &:first-of-type {
      width: 25%;
    }
  }

  .cart__el {
    display: flex;
    justify-content: space-between;
    flex-wrap: nowrap;
    border-bottom: 1px solid $gray;
    padding: 2em 0em;

    // &:last-of-type {
    //   border: none;
    // }

    &__item {
      width: 23%
    }
  }

  .total {
    text-align: right;
    padding: 1em 0em;
    // border-top: 1px solid $gray;
    // padding-top: 1em;

    p {
      margin: 0;


      &:last-of-type {
        padding: 0.4em;
      }
    }

    button {
      border : none;
      outline : none;
    }
  }

  .cart__image {
    display: flex;

    > div:first-of-type {
      width: 50%;
      margin-right: 5%;
    }

    > div:last-of-type {
      font-size: 14px ;
      text-align: left;
    }
  }
}

sources.styles/pages/_about.scss:
.about-info {

  &__ el {
    margin-bottom: 2em;

    &:last-of-type {
      margin-bottom: 0em;
    }
  }
}

.call-out {
  padding: 4em 0em;

  &__el {
    margin-bottom: 2em;

    &:last-of-type {
      margin-bottom: 0em;
    }
  }

  &__icon {
    max-width: 120px;
  }
}

.icon-set {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-row-gap: 60px;
  padding-top: 2em;
  max-width: 500px;
  // display: none;
}

.call-out__icon {
  height: 80px;
  width: 80px;
}

.team-parent {
  padding-top: 4em;
}

@media(min-width: $bp-md) {
  .about-info {
    display: flex;
    justify-content: space-between;

    &__el {
      margin-bottom: 0em;
      width: 40%;

      &:last-of-type {
        width: 51%;
      }
    }
  }

  .call-out {
    padding: 8em 0em;
    display: flex;
    flex-wrap: nowrap;
    justify-content: space-between;
    align-items: center;

    &__el {
      width: 45%;
      margin-bottom: 0em;
    }
  }

  .icon-set {
    padding-top: 0em;
  }
}

.team {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-gap: 40px;
  grid-column: 40px;
  padding-top: 2em;
  padding-bottom: 4em;

  @media(min-width: $bp-md) {
    grid-template-columns: 1fr 1fr 1fr;
  }

  @media(min-width: $bp-lg) {
    grid-template-columns: 1fr 1fr 1fr 1fr;
  }

  &__el {
    text-decoration: none;

    .image-el { overflow: hidden;}

    img { transition: all 600ms $ease-out-quart; }

    &:hover {

      img {
        transform: scale(1.1);
      }
    }

    p { 
      margin: 0; 
      color: #444 ;
      font-size: 16px;
    }

    p:first-of-type {
      padding-top: 0.1em;
      padding-bottom: 0em;
      color: $primary;
    }

    p:last-of-type {
      font-style: italic;
    }
  }
}


@media(min-width: $bp-lg) {
  .about-info {
    align-items: center;
  }
}

sources.styles/pages/_contact.scss:
.push-bottom {
  padding-bottom: 1em;
}

.contact-page {
  h4 {
    font-size: 1.2em;
    color: $primary;
    padding-bottom: 0.4em;
  }

  h1 {
    font-size: 2.2em;
  }

}

.re {
  position: relative;
}

.contact__logo {
  max-width: 240px;
  margin: auto;
  padding: 2em 0em 0em 0em;

  svg {
    width: 100%;
  }
}

.contact {
  padding: 3em 0em;
  display: flex;
  flex-direction: column-reverse;
  position: relative;

  &__el {
    position: relative;
    z-index: 5;

    &:first-of-type {
      background: white;
      box-shadow: 0px 0px 30px rgba(black, 0.1);
      border-radius: 8px;

      form {
        padding: 1em;
      }

    }

  }

  .contact__form {
    width: 90%;
    margin: auto;
    margin-top: 1em;
    padding-bottom: 2em;
  }

  .hbspt-form {
    width: 100%;
    * {
      width: 100%;
    }
  }

  label {
    display: block;
    padding-bottom: 0.6em;
  }

  input {
    width: calc(100% - 0.5em);
    height: 50px;
    font-size: 1em;
    outline: none;
    -webkit-appearance: none;
    border-radius: 3px;
    border: none;
    background: #f2f2f2;
  }

  textarea {
    font-size: 1em;
    outline: none;
    padding: 1em;
    width: calc(100% - 2em);
    border: none;
    background: #f2f2f2;
    border-radius: 3px;
  }

  .button-el {
    padding-top: 1em;

    button {
      outline: none;
      border: none;
      -webkit-appearance: none;
      @extend .cta;
    }
  }
}

.platforms {
  margin: 0; 
  padding: 0;
  list-style-type: none;
  display: flex;
  justify-content: space-between;
  flex-wrap: wrap;
  padding-bottom: 2em;

  li {  
    margin-bottom: 1.2em;
    width: 47%;
    font-weight: bolder;

    &:last-of-type {
      margin-bottom: 0em;
    }

    a {
      display: inline-flex;
      width: 100%;
      align-items: center;
      text-decoration: none;
      color: #444;

      span:first-of-type {
        margin-right: 10px;
      }

      svg {
        position: relative;
        top: 4px;
        stroke: $primary !important;
      }
    }
  }
}


.offset-el {
  position: absolute;
  top: 0; right: 0;
  width: 80%;
  height: 100%;
  overflow: hidden;
  background: #f2f2f2;
  display: none;

  .section-bg__el {
    filter: blur(3px);
  }
}


@media(min-width: $bp-md) {

  .push-bottom {
    padding-bottom: 3em;
  }

  .offset-el {
    display: block;
  }

  .contact {
    justify-content: space-between;
    flex-direction: row;
    padding: 6em 0em;

    &__el {
      width: 55%;

      &:first-of-type {
        background: white;
        box-shadow: 0px 0px 30px rgba(black, 0.1);
        border-radius: 8px;
        margin-bottom: 0em;

        form {
          padding: 2em;
        }
      }

      &:last-of-type {
        padding: 0em 3em;
        width: 40%;
      }
    }
  }

  .platforms {
    display: flex;
    justify-content: space-between;
    flex-wrap: wrap;

    li {  
      margin-bottom: 0.7em;
      width: 47%;

      &:last-of-type {
        margin-bottom: 0em;
      }

      a {
        display: flex;
        align-items: flex-start;
        flex-direction: column;


        span:first-of-type {
          margin-right: 0px;
        }

        svg {
          position: relative;
          top: 5px;
          stroke: $primary !important;
        }
      }
    }
  }

}

@media(min-width: $bp-lg) {
  .platforms {

    li a {
      flex-direction: row;
      align-items: center;

      span:first-of-type {
        margin-right: 10px;
      }
    }
  }
}

sources.styles/pages/_collection.scss:
.collection__grid {
  padding: 0em 0em 5em 0em;
}

.collection-grid__row {
  display: grid;
  grid-template-columns: 1fr;
  grid-row-gap: 40px;
  padding-top: 2em;

  @media(min-width: $bp-md) {
    grid-template-columns: 1fr 1fr;
    grid-gap: 40px;
  }

  @media(min-width: $bp-lg) {
    grid-template-columns: 1fr 1fr 1fr;
  }

}

.collection-grid__inner {
  padding: 2em 1em;
}

.collection-grid__item {
  border: 10px solid $gray;
}

.prod-des {
  text-align: center;
}

.filter {
  text-align: center;
  // display: inline-flex;
  align-items: center;
  justify-content: center;
  margin: auto;
  width: 100%;

  h4 {
    padding: 1em 0em;
    font-size: 1.1em;
    margin-right: 0.7em;
  }

  &__options {
    list-style-type: none;
    margin: 0; padding: 0;
    // display: flex;
    text-align: center;

    li {
      // margin: 0px 0.7em;
      margin: 1em 0em;

      &.active {

        a {
          font-weight: 800;
        }
      }
    }

    a {
      color: $primary;
    }
  }
}

.mini-blurb {
  margin: 0 !important;
  font-weight: 700;
  color: #999;
  padding-top: 0.3em;
}

.price-cc {
  margin-top: 0.3em;
}


@media(min-width: $bp-md) {
  .filter {
    text-align: center;
    // display: inline-flex;

    &__options {
      display: flex;
      justify-content: center;
      text-align: center;

      li {
        margin: 0px 0.7em;
      }

    }
  }
} 

