sources.styles/components/nav/mega-nav/_mega-nav:
#mega-menu-container {
  border-top: 1px solid $gray;
  position: absolute;
  display: none;
  visibility: hidden;
  width: 100%;
  top: 100%;
}

#mega-nav {
  background: white;
  opacity: 0;
  transform: translateY(-50px);

  > .inner {
      padding: 1.3em 0em;
    }
}

.mega {
  display: flex;
  justify-content: space-between;

  &__el:first-of-type {
    width: 60%;
  }

  &__el {
    width: 35%;
  }
}

.is-mega-nav__el {
  h5 {
    transform: translateY(-30px);
    opacity: 0;

    a {
      color: $primary;
      text-decoration: none;
    }

  }
  li {
    transform: translateY(-15px);
    opacity: 0;
  }

  &.active {

    h5 {
      transition: all 1100ms ease;
      transform: translateY(0px);
      opacity: 1;
    }

    @for $i from 0 through 20 {

      li:nth-child(#{$i}) {
        transition: all 1100ms ease;
        opacity: 1;
        transform: translateY(0px);
        transition-delay: 120ms * $i;
      }
    }
  }
}


.mega-showing {

  #mega-menu-container {
    visibility: visible;
    display: block;
  }

  #mega-nav {
    transition-delay: 400ms;
    transition: all 400ms ease;
    opacity: 1;
    transform: translateY(0px);
  }
}


.section-bg {
  @include fill-parent();

  &__el {
    @include fill-parent();
    background-size: cover;
    background-position: center;
  }
}

.loaded {
  #mega-menu-container {
    opacity: 1 !important;
  }
}

.mega__slider {
  padding-top: 1.3em;
  position: relative;
  width: 100%;
}

.mega__slideitem {
  width: 100%;
  position: relative;
  display: inline-block;
  overflow: hidden;
  opacity: 0;
  transform: translateY(-20%);
  text-decoration: none;
  color: $dark;

  &:hover {

    .mega__slideitem__title {
      font-weight: 800;
    }

    .mega-prod__name {
      opacity: 1;
      transform: scale(1);
    }

    .prod-name-mega {
      opacity: 1;
      transform: translateY(-50%);
    }
  }

  @media(min-width: $bp-md) {
    min-width: 240px;
    width: 30%;
    margin-right: 3%;
  }

  @media(min-width: $bp-xl) {
    width: 30%;
    margin-right: 3%;
  }
}


.mega__slideitem__title {
  text-align: center;
  position: relative;
  top: 0;
  background: white;
}

.mega__slideitem {
  display: inline-block;
}


.mega-slide-image {
  position: relative;
  background: $gray;
  background-size: cover;
  background-position: center;
  transition: all 300ms ease;
}

.mega-prod__name {
  @include fill-parent();
  background: rgba(black, 0.7);
  z-index: 6;
  transition: all 200ms ease;
  opacity: 0;
  transform: scale(0.98);
}

.prod-name-mega {
  margin: 0;
  width: 100%;
  text-align: center;
  position: absolute;
  top: 50%;
  z-index: 9;
  opacity: 0;
  transform: translateY(-10%);
  color: white;
  font-weight: 600;
  font-size: 0.9em;
  transition: transform 200ms, opacity 200ms ease;
}

.mega-set {
  @for $i from 0 through 15 {
    .mega__slideitem:nth-child(#{$i}) {
      transition: all 1200ms $ease-out-quart;
      opacity: 1;
      transform: translateY(0px);
      transition-delay: 110ms * $i;
    }
  }
}

.visy {
  opacity: 0;
  transform: translateY(-10px);
}

.user-enabled {
  .mega__slideitem {
    transition: none !important;
  }

  .visy {
    opacity: 1;
    transform: translateY(0px);
    transition: transform 1200ms, opacity 1500ms ease;
  }
}

.is-mega-nav {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;

  &__el {
    width: 45%;
  }

  &__el:first-of-type {
    margin-bottom: 1em;
  }
}

.is-mega-nav ul {
  list-style-type: none;
  margin: 0;
  padding: 1em 0em;
  font-size: 14px;

  h5 a {
    color: $primary;
    text-decoration: none;
  }

  li {
    margin-bottom: 1em;
  }

  li:last-of-type {
    margin-bottom: 0em;
  }

  a {
    color: $dark;
    text-decoration: none;
    transition: all 400ms ease;

    &:hover {
      color: $primary;
    }
  }
}

