    .mn-lin {
      width: 40%;
      left: 30%;

      &-top {
        top: 8px;  
        transform: translateY(6px) rotate(45deg);
      }

      &-mid {
        top: 14px;  
        display: none;
      }

      &-btm {
        top: 20px;  
        transform: translateY(-6px) rotate(-45deg);
      }
    }
  }
}

