      width: 100%;
      display: inline-flex;
      flex-direction: column;

      &:after {
        content: 'flickity';
        display: none;
      }
    }

    li {
      margin-bottom: 2em;
      position: relative;
      cursor: pointer;
      color: #aaa;
      margin-right: 10px;
      min-width: 120px;
      font-size: 18px;

      &:last-of-type {
        margin-bottom: 0;
      }
      svg {
        display: none;
      }

      svg {
        opacity: 0;
        transform: translateX(-20px);
      }

      span:last-of-type {
        margin-left: 2em;
      }

      &.active, &:hover {
        color: #ff8f58;

        svg {
          transition: all 400ms ease;
          opacity: 1;
          transform: translateX(0px) rotate(180deg);
        }

        &:after { background: #ff8f58; }
      }


      &:after {
        content: '';
        position: absolute;
        height: 1px;
        background: #aaa;
        width: 100%;
        bottom: 0;
        left: 0;
      }
    }
  }
}


.category__content {
  background: $primary;
  position: relative;
  color: white;
  border-radius: 10px;

  &-inner {
    padding: 3em 1.5em;
    position: relative;
  }

  a {
    color: white;
  }

 &-item {
   display: none;

   &.active {
     display: block;
   }
 }
}


.boxy-el {
  position: absolute;
  top: 100%;
  right: 10%;
  transform: translateY(-50%);

  rect {
    fill: white;
  }
}


@media(min-width: $bp-md) {
  .category-parent {
    background: #FAF7F7;

    padding: 4em 0em;
  }

  .category {
    display: flex;
    flex-direction: row;
    align-content: stretch;


    &__el {
      width: 50%;

      &.has-content {
        width: calc(50% + 90px);
      }
    }

    &__list {
      background: white;
      padding: 8em 3em;
      box-shadow: 0px 0px 40px rgba(black, 0.1);
      border-radius: 10px;

      ul {
        display: block;

        &:after {
          content: '';
        }
      }

      li {
        display: flex;
        color: #aaa;
        align-items: center;
        margin-right: 0px;
        min-width: auto;
        font-size: 1em;
        svg {
          display: block;
        }
      }
    }
  }


  .category__content {
    background: $primary;
    position: relative;
    margin-top: 90px;
    left: -90px;
    color: white;
    border-radius: 10px;

    &-inner {
      padding: 6em 3em;
    }

   &-item {
     display: none;

     &.active {
       display: block;
     }
   }
  }


  .boxy-el {
    position: absolute;
    top: 100%;
    right: 10%;
    transform: translateY(-50%);

    rect {
      fill: white;
    }
  }
