sources.styles/components/_byob.scss:
.type {
	margin-bottom: 2em;
}

.dropdown-parent {
	position: relative;
}

.dropdown {
	cursor: pointer;

	&__name {
		padding: 1em 0em;
		display: block;
		width: 100%;
		border: 1px solid #eee;

		span {
			display: inline-block;
			padding-left: 1em;
		}
	}

	nav {
		box-sizing: border-box;
		border-left: 1px solid #eee;
		border-right: 1px solid #eee;
		border-bottom: 1px solid #eee;
	}
}

.dropdown-parent {
	margin-bottom: 2em;
}

.dropdown-parent nav {
	display: none;
	position: absolute;
	width: 100%;
	background: white;
	z-index: 5;
}

.dropdown-parent nav ul {
	margin: 0;
	padding: 0;
	list-style-type: none;
	padding-bottom: 0.5em;


	li {
		position: relative;

		&:last-of-type {
			margin-bottom: 0;
		}

		&:hover, &:focus {
			background: #eee;
		}
	}

	input {
		position: absolute;
		left: -300%;

		&:checked {

			+ label {

				.check-box-icon {
					display: block;
				}
			}
		}
	}
}

.check-box {
	display: inline-block;
	width: 24px;
  height: 24px;
  position: relative;
	border-radius: 3px;
	border: 2px solid #ddd;
}

.check-box-icon {
	width: 130%;
	position: relative;
	left: -15%;
	display: none;

	svg {
		width: 100%;
	}
}

.label {
	display: flex;
	flex-direction:row;
	align-items: center;
	padding: 0.8em 0.4em;
	cursor: pointer;
}

.product-name {
	padding-left: 0.6em;
	display: block;
	width: 100%;
}

.dropdown-parent {

	&.active {

		.dropdown__name {
			border-bottom: 0px;
		}

		.carrot svg {
			transform: rotate(180deg);
		}
	}
}
.dropdown-parent.active nav {
	display: block;
	width: 100%; 
}

.dropdown__name {
	position: relative;
	display: block !important;
	width: 100%;
}

.carrot {
	position: absolute;
	top: 50%;
	transform: translateY(-50%);
	right: 16px;
	width: 16px;

	svg {
		width: 100% !important;
    transition: all 200ms ease;

    path {
      fill: #da6227;
    }
	}
}

